mapper.update:

	UPDATE POOL_MEMBER
	   SET APPROVE = 'Y'
	 WHERE USR_SEQ  = #{usrSeq }
	   AND POOL_SEQ = #{poolSeq }
	   AND APRV_USR = #{aprvUsr }

mapper.delete:

	DELETE 
	  FROM POOL_MEMBER
	 WHERE USR_SEQ  = #{usrSeq }
	   AND POOL_SEQ = #{poolSeq }
	   AND APPROVE = 'N'
	   AND APRV_USR = #{aprvUsr }
