mapper.select:

	UPDATE POOL_PARTY
	   SET POOL_NAME = #{poolName }
	
	
	   
	   	,POOL_COMMENT  = #{poolComment }
	   
	
	
	   
	   	,POOL_PIC  = #{poolPic }
	   
	
	
	   
	   	,CITY_SEQ  = #{citySeq }
	   
	  
	
	   
	   	,MANAGER_EMAIL  = #{managerEmail }
	   
	    
	 
	   
	   	,POOL_PUBLIC_YN  = #{poolPublic_yn }
	   
	 
	 
	WHERE POOL_SEQ = #{poolSeq }
